import { useRef, useState } from "react";
import "../css/ImgBox.css";

function ImgBox(props) {
  const [modal, setModal] = useState("");
  const modalRef = useRef("");

  const onErrorImg = (err) => {
    err.target.className = "error";
  };

  const onClick = (event) => {
    setModal(event.target.currentSrc);
    if (modal === "") {
      modalCheck(event.target.currentSrc);
    } else {
      modalCheck();
    }
  };

  const closeModal = () => {
    modalCheck();
  };

  const bgClick = (event) => {
    const bg = event.target.className;
    if (bg === "modalContanier") {
      modalRef.current.className = "close";
    }
  };

  function modalCheck(img) {
    let triger = modalRef.current.className;
    if (triger === "") {
      triger = img;
      modalHandler(triger);
    } else {
      triger = modalRef.current.className;
      modalHandler(triger);
    }
  }

  function modalHandler(triger) {
    if (triger === "close") {
      modalRef.current.className = "modalContanier";
    } else {
      modalRef.current.className = "close";
    }
  }

  return (
    <div className="container">
      <div className="row">
        <div className="modalContanier close" onClick={bgClick} ref={modalRef}>
          <form className="modal">
            <img src={modal}></img>
            <h1>Ïù¥Í≤ÉÏùÄ Î™®Îã¨Ï∞ΩÏù¥Ïöî</h1>
            <button onClick={closeModal}>‚ùå</button>
            <button>üíò</button>
            <button>üíå</button>
          </form>
        </div>
        {props?.imgURLArr.map((img, index) => (
          <img
            onClick={onClick}
            className="box"
            onError={onErrorImg}
            alt=""
            key={index}
            src={img}
          />
        ))}
      </div>
    </div>
  );
}

export default ImgBox;
